targets.target:
#!/bin/sh -xe apt-get update -y apt-get install -y apache2
echo 'Hello from Apache deployed by SlipStream!' > /var/www/data.txt
service apache2 stop port=$(ss-get port) sed -i -e 's/^Listen.*$/Listen '$port'/' /etc/apache2/ports.conf sed -i -e 's/^NameVirtualHost.*$/NameVirtualHost *:'$port'/' /etc/apache2/ports.conf sed -i -e 's/^<VirtualHost.*$/<VirtualHost *:'$port'>/' /etc/apache2/sites-available/default service apache2 start ss-set ready true url="http://$(ss-get hostname):$port" ss-set url.service $url ss-set ss:url.service $url
